package com.victor.che.pay.wxpay.alipay;import android.text.TextUtils;import java.util.Map;public class PayResult {	private String resultStatus;	private String result;	private String memo;/*	if (data["payState"]  == 1) {		alert('支付宝支付成功');	}else if (data["payState"] == -1) {		alert('支付宝取消支付');	}else if (data["payState"] == 0) {		alert('支付宝支付失败');	}*/	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}